mapper.update:
update product
set name=#{name}, price=#{price}, menuId=#{menuId}, submenuId=#{submenuId}, details=#{details}, factory=#{factory}
where id=#{id}

mapper.select:
select id, name, price, menuId, submenuId, details, factory
from product
where id=#{id}

select id, name, price, menuId, submenuId, details, factory
from product
where menuid=#{menuid}

select id, name, price, menuId, submenuId, details, factory
from product
where submenuId=#{submenuId}
